mapper.update:
UPDATE PRO_JOIN
SET CHAPTER = #{num}
WHERE PJID = #{pjId}

mapper.select:
SELECT CHAPTER
FROM PRO_JOIN
WHERE PJID = #{pjId}

SELECT NVL(PROID, 0)
FROM PRO_LEADER_VOTE_START
WHERE VOTER = #{pjId}

SELECT PJID
FROM
(
    SELECT PJID, PJDATE, USID, PID
    FROM PRO_JOIN
    WHERE PJID NOT IN (SELECT PJID
                       FROM PRO_EXIT)
)
WHERE PID = #{pid}
  AND USID = #{usId}

SELECT USID
FROM PRO_JOIN
WHERE PJID = #{pjId}

SELECT NVL(PROID, 0)
FROM PRO_LEADER_VOTE_START
WHERE VOTER = #{voter}

SELECT NVL(SKID, 0)
FROM PRO_SKILL
WHERE PID = #{pid}

SELECT MAX(PROID) AS PROID
FROM PRO_LEADER_PROPOSE
WHERE PID = #{pid}

mapper.insert:
{ CALL PROC_TIMELINE_INSERT(#{pjId, jdbcType=INTEGER, mode=IN}
						  , #{pid, jdbcType=INTEGER, mode=IN}
						  , #{content, jdbcType=VARCHAR, mode=IN}
						  , #{pfile, jdbcType=VARCHAR, mode=IN}) }

{ CALL PROC_VISITOR_INSERT(#{usId, jdbcType=INTEGER, mode=IN}
						 , #{pid, jdbcType=INTEGER, mode=IN}
						 , #{vsText, jdbcType=VARCHAR, mode=IN} )

mapper.select:
SELECT PROID
FROM PRO_CREATE
WHERE PID = #{pid}

SELECT PPID
FROM PRO_PROGRESS
WHERE PID=#{pid}

SELECT P.GPID AS GPID, R.SSNAME AS SSNAME 
FROM              
(        
    SELECT GPID, SSID
    FROM PRO_GUSIDE_PLAYTERM
    WHERE MODEID = #{modeId}
      AND PROID = #{proId}
) P LEFT JOIN REPORT R
ON P.SSID = R.SSID

SELECT MIN(P.GPID) + 2 AS MINGPID
FROM              
(        
    SELECT GPID, SSID
    FROM PRO_GUSIDE_PLAYTERM
    WHERE MODEID = #{modeId}
      AND PROID = #{proId}
) P LEFT JOIN REPORT R
ON P.SSID = R.SSID

SELECT GPID, SSNAME
FROM
(
    SELECT P.GPID AS GPID, R.SSNAME AS SSNAME 
    FROM              
    (        
        SELECT GPID, SSID
        FROM PRO_GUSIDE_PLAYTERM
        WHERE MODEID = #{modeId}
          AND PROID = #{proId}
    ) P LEFT JOIN REPORT R
    ON P.SSID = R.SSID
)
WHERE GPID = #{gpId}

SELECT TRUNC(TO_DATE(PEDATE, 'YYYY-MM-DD') - TO_DATE(SYSDATE, 'YYYY-MM-DD'), 2)
FROM PRO_PROGRESS
WHERE PID = #{pid}

mapper.delete:
DELETE
FROM PRO_GUSIDE_EVALUATION_LIKE
WHERE SAID = #{saId}
  AND USID = #{usId}

mapper.select:
SELECT COUNT(*) 
FROM PRO_GUSIDE_EVALUATION_LIKE
WHERE USID = #{usId}
  AND SAID = #{saId}

SELECT COUNT(*) 
FROM PRO_GUSIDE_EVALUATION_LIKE
WHERE SAID = #{saId}

SELECT NICKNAME
FROM USERINSERT
WHERE USID = #{usId}

SELECT ENDCHECK
FROM PRO_JOIN
WHERE PID = #{pid}
  AND USID = #{usId}
  AND PJID NOT IN (SELECT PJID
                    FROM PRO_EXIT)

SELECT PDCHECK
FROM PRO_DESTROY_VOTE_PROGRESS
WHERE USID = #{pjId}
  AND PID = (SELECT MAX(PID)
            FROM PRO_DESTROY_VOTE_PROGRESS
            WHERE USID = #{pjId});
